import Link from 'next/link';


const FieldTypes = [
  {
    id: 'image',
    type: 'image',
    defaultValue: "",
  },
  {
    id: 'name',
    type: 'text',
    defaultValue: "Cayce Pollard",
  },
  {
    id: 'profile',
    type: 'text',
    defaultValue: "Neural Interface Designer",
  }
]

const Field = ({ type, id, defaultValue, edit }) => {
 const [value, setValue] = React.useState(defaultValue) 
  return <div className={`field ${type} ${edit === type ? 'active' : ''}`} onClick={() => editField(type)} >{type === 'image' ? (
    <>
    {value ? 
      <img src={value} /> : 
      <div 
        className={`field placeholder image ${edit === 'image' ? 'active' : ''}`} />}
      </>
  ) : type === 'text'  ?(
      <h1>{value}</h1>
  ) : null}</div>
}

const Build = () => {
  // const [image, setImage] = React.useState()
  const [edit, setEdit] = React.useState(null)
  // const [name, setName] = React.useState("")
  // const [profile, setProfile] = React.useState("")
  const editField = (type) => {
    setEdit(type)
  } 
  
    return (
        <div className="container">            
          <div className="sidebar">
            Sidebar
            <br />
            {edit === "profile"  && (
              <input value={profile} onChange={(e) => setProfile(e.target.value)} />
            )}
            {edit === "image"  && (
              <input value={image} onChange={(e) => setImage(e.target.value)} />
            )}
            {edit === "name"  && (
              <input value={name} onChange={(e) => setName(e.target.value)} />
            )}
          </div>
            <div className="header">
              <div className="field userimage" onClick={() => editField('image')}>
                
              </div>
              <div className={`field name ${edit === 'name' ? 'active' : ''}`} onClick={() => editField('name')} >
                <h2>{name}</h2>
                
              </div>
              <div >
                <h4>{profile}</h4>
                {/* <input value={userprofile} onChange={(e) => setProfile(e.target.value)} /> */}
              </div>
            </div>
            <style jsx>{`
              .sidebar {
                width: 200px;
                position: fixed;
                top: 0;
                left: 0;
                height: 100vh;
                overflow: auto;
                background: #222;
                color: #fff;
              }
              .container {
                width: 80%;
                max-width: 720px;
                margin: 0 auto;
              }

              .header {
                display: flex;
                flex-direction: column;
                align-items: center;
              }

              .field:hover {
                border: 1px solid lightblue;
              }
              .field.active {
                border: 2px solid navy;
                border-radius: 3px;
              }
              .field {
                padding: 10px 20px;
                margin-top: 20px;
                text-align: center;
              }

              .placeholder {
                width: 50px;
                height: 50px;
                border-radius: 50%;
                background: #aaa;
              }
              
            `}</style>
        </div>
    )
}

export default Build;
