import Link from 'next/link';

const Build = () => {
  const [userImage, setUserImage] = React.useState()
  const [edit, setEdit] = React.useState(null)
  const [username, setUserName] = React.useState("Cayce Pollard")
  const [userprofile, setProfile] = React.useState("Neural Interface Designer")
  const editField = (type) => {
    setEdit(type)
  } 
  
    return (
        <div className="container">            
          <div className="sidebar">
            Sidebar
          </div>
            <div className="header">
              <div className="field userimage">
                {userImage ? 
                  <img src={userImage} /> : 
                  <div 
                    className={`field placeholder image ${edit === 'image' ? 'active' : ''}`} 
                    onClick={() => editField('image')} />}
              </div>
              <div className={`field name ${edit === 'name' ? 'active' : ''}`} onClick={() => editField('name')} >
                <h2>{username}</h2>
                {/* <input value={username} onChange={(e) => setUserName(e.target.value)} /> */}
              </div>
              <div className={`field profile ${edit === 'profile' ? 'active' : ''}`} onClick={() => editField('profile')}>
                <h4>{userprofile}</h4>
                {/* <input value={userprofile} onChange={(e) => setProfile(e.target.value)} /> */}
              </div>
            </div>
            <style jsx>{`
              .sidebar {
                width: 300px;
                position: fixed;
                top: 0;
                left: 0;
                height: 100vh;
                overflow: auto
              }
              .container {
                width: 80%;
                max-width: 720px;
                margin: 0 auto;
              }

              .header {
                display: flex;
                flex-direction: column;
                align-items: center;
              }

              .field:hover {
                border: 1px solid lightblue;
              }
              .field.active {
                border: 2px solid navy;
                border-radius: 3px;
              }
              .field {
                padding: 10px 20px;
                margin-top: 20px;
                text-align: center;
              }

              .placeholder {
                width: 50px;
                height: 50px;
                border-radius: 50%;
                background: #aaa;
              }
              
            `}</style>
        </div>
    )
}

export default Build;
