import Link from 'next/link';

const Build = () => {
  const [userImage, setUserImage] = React.useState()
  const [username, setUserName] = React.useState("Cayce Pollard")
  const [userprofile, setProfile] = React.useState("Neural Interface Designer")
    return (
        <div className="container">            
            <div className="header">
              <div className="field userimage">
                {userImage ? <img src={userImage} />: <div className="placeholder" />}
              </div>
              <div className="field user-name">
                <input value={username} onChange={(e) => setUserName(e.target.value)} />
              </div>
              <div className="field profile">
                <input value={userprofile} onChange={(e) => setProfile(e.target.value)} />
              </div>
            </div>
            <style jsx>{`
              .container {
                width: 80%;
                max-width: 720px;
                margin: 0 auto;
              }

              .header {
                display: flex;
                flex-direction: column;
                align-items: center;
              }

              .field:hover {
                border: 1px solid lightblue;
              }
              .field {
                margin-top: 20px;
                text-align: center;
              }

              .placeholder {
                width: 50px;
                height: 50px;
                border-radius: 50%;
                background: #aaa;
              }
              
            `}</style>
        </div>
    )
}

export default Build;
