import Link from 'next/link';

const Build = () => {
    const [userImage, setUserImage] = React.useState()
    const [username, setUserName] = React.useState()
    const [userprofile, setProfile] = React.useState()
    return ( <
        div className = "container" >
        <
        div className = "header" >
        <
        div className = "field userimage" > { userImage ? < img src = { userImage } />: <div className="placeholder" / > } <
        /div> <
        div className = "field user-name" >
        <
        input value = { username }
        onChange = {
            (e) => setUserName(e.target.value) }
        /> <
        /div> <
        div className = "field profile" >
        <
        input value = { userprofile }
        onChange = {
            (e) => setProfile(e.target.value) }
        /> <
        /div> <
        /div> <
        style jsx > { `
              .container {
                width: 80%;
                max-width: 720px;
                margin: 0 auto;
              }

              .header {
                display: flex;
                flex-direction: column;
                align-items: center;
              }

              .field {
                margin-top: 20px;
              }
              
            ` } < /style> <
        /div>
    )
}

export default Build;
